import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import './Coordinator.css';

const CoordinatorMessengers = () => {
  const [messengers, setMessengers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    phone: '',
    area: '',
    employeeId: '',
  });

  useEffect(() => {
    fetchMessengers();
  }, []);

  const fetchMessengers = async () => {
    try {
      setLoading(true);
      const response = await api.get('/coordinator/messengers');
      setMessengers(response.data);
    } catch (error) {
      console.error('Error fetching messengers:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleAddMessenger = async (e) => {
    e.preventDefault();
    try {
      await api.post('/coordinator/messengers', formData);
      alert('Messenger created successfully');
      setFormData({
        name: '',
        email: '',
        password: '',
        phone: '',
        area: '',
        employeeId: '',
      });
      setShowForm(false);
      fetchMessengers();
    } catch (error) {
      alert('Error creating messenger: ' + error.response?.data?.error);
    }
  };

  if (loading) {
    return <div className="loading">Loading messengers...</div>;
  }

  return (
    <div className="coordinator-container">
      <div className="page-header">
        <h1>Manage Messengers</h1>
        <button className="btn-primary" onClick={() => setShowForm(!showForm)}>
          {showForm ? '‚úï Cancel' : '+ Add New Messenger'}
        </button>
      </div>

      {showForm && (
        <div className="form-container">
          <form onSubmit={handleAddMessenger} className="coordinator-form">
            <div className="form-grid">
              <div className="form-group">
                <label>Full Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Employee ID</label>
                <input
                  type="text"
                  name="employeeId"
                  value={formData.employeeId}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Phone</label>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Area/Zone</label>
                <input
                  type="text"
                  name="area"
                  value={formData.area}
                  onChange={handleInputChange}
                />
              </div>
            </div>
            <button type="submit" className="btn-primary">‚úì Create Messenger</button>
          </form>
        </div>
      )}

      <div className="messengers-grid">
        {messengers.map((messenger) => (
          <div key={messenger._id} className="messenger-card">
            <div className="card-header">
              <h3>{messenger.name}</h3>
              <span className={`status-badge ${messenger.isActive ? 'active' : 'inactive'}`}>
                {messenger.isActive ? 'üü¢ Active' : 'üî¥ Inactive'}
              </span>
            </div>
            <div className="card-body">
              <p><strong>Email:</strong> {messenger.email}</p>
              <p><strong>Phone:</strong> {messenger.phone || 'N/A'}</p>
              <p><strong>Area:</strong> {messenger.area || 'N/A'}</p>
              <p><strong>Employee ID:</strong> {messenger.employeeId || 'N/A'}</p>
            </div>
            <div className="card-actions">
              <button className="action-btn edit">‚úé Edit</button>
              <button className="action-btn delete">üóëÔ∏è Remove</button>
            </div>
          </div>
        ))}
      </div>

      {messengers.length === 0 && (
        <div className="empty-state">
          <p>No messengers yet. Add one to get started.</p>
        </div>
      )}
    </div>
  );
};

export default CoordinatorMessengers;
