import React, { useEffect, useRef } from 'react';

// Simple LocationPicker using Google Maps JS API + Places Autocomplete.
// Expects REACT_APP_GOOGLE_MAPS_API_KEY to be set in environment.
// Props: value: { address, lat, lng }  onChange(newValue)
const LocationPicker = ({ value = {}, onChange }) => {
  const mapRef = useRef(null);
  const inputRef = useRef(null);
  const markerRef = useRef(null);

  useEffect(() => {
    const key = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
    if (!key) {
      console.warn('LocationPicker: REACT_APP_GOOGLE_MAPS_API_KEY not set');
      return;
    }

    const loadScript = () => {
      if (window.google && window.google.maps) return Promise.resolve();
      return new Promise((resolve, reject) => {
        const s = document.createElement('script');
        s.src = `https://maps.googleapis.com/maps/api/js?key=${key}&libraries=places`;
        s.async = true;
        s.defer = true;
        s.onload = resolve;
        s.onerror = reject;
        document.head.appendChild(s);
      });
    };

    let map;
    loadScript()
      .then(() => {
        const google = window.google;
        const center = value.lat && value.lng ? { lat: value.lat, lng: value.lng } : { lat: 6.9271, lng: 79.8612 };
        map = new google.maps.Map(mapRef.current, { center, zoom: 14 });

        markerRef.current = new google.maps.Marker({ position: center, map });

        const autocomplete = new google.maps.places.Autocomplete(inputRef.current, { fields: ['formatted_address','geometry'] });
        autocomplete.addListener('place_changed', () => {
          const place = autocomplete.getPlace();
          if (!place.geometry) return;
          const lat = place.geometry.location.lat();
          const lng = place.geometry.location.lng();
          const address = place.formatted_address;
          map.panTo({ lat, lng });
          markerRef.current.setPosition({ lat, lng });
          onChange && onChange({ address, lat, lng });
        });

        map.addListener('click', (e) => {
          const lat = e.latLng.lat();
          const lng = e.latLng.lng();
          markerRef.current.setPosition({ lat, lng });
          // reverse geocode to get address
          const geocoder = new google.maps.Geocoder();
          geocoder.geocode({ location: { lat, lng } }, (results) => {
            const address = results && results[0] ? results[0].formatted_address : '';
            onChange && onChange({ address, lat, lng });
            inputRef.current.value = address;
          });
        });
      })
      .catch((err) => {
        console.error('Failed to load Google Maps script', err);
      });

    return () => {
      // nothing to cleanup for now
    };
  }, [value, onChange]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
      <input
        ref={inputRef}
        type="text"
        placeholder="Search or click on the map to choose location"
        defaultValue={value.address || ''}
        style={{ padding: '8px', borderRadius: 4, border: '1px solid #ccc' }}
      />
      <div ref={mapRef} style={{ width: '100%', height: 320, borderRadius: 6 }} />
    </div>
  );
};

export default LocationPicker;
