import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../utils/AuthContext';
import './Dashboard.css';

const Dashboard = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  const menuItems = user?.role === 'messenger' 
    ? [
        { icon: 'ðŸšš', title: 'My Routes', description: 'View assigned routes and tasks', path: '/messenger/routes' },
        { icon: 'ðŸ“¦', title: 'Deliveries', description: 'Track delivery status', path: '/messenger/deliveries' },
        { icon: 'ðŸ“·', title: 'Upload Proof', description: 'Submit proof of delivery', path: '/messenger/proof' },
      ]
    : [
        { icon: 'ðŸ‘¥', title: 'Messengers', description: 'Manage messenger accounts', path: '/coordinator/messengers' },
        { icon: 'ðŸ“‹', title: 'Bills', description: 'Input and manage bills', path: '/coordinator/bills' },
        { icon: 'ï¿½', title: 'Tracking', description: 'Real-time delivery tracking', path: '/coordinator/tracking' },
        { icon: 'V', title: 'Verification', description: 'Verify delivery proofs', path: '/coordinator/verification' },
        { icon: 'ðŸ“ˆ', title: 'Reports', description: 'Generate DSR and analytics', path: '/coordinator/reports' },
      ];

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Welcome, {user?.name}!</h1>
        <p className="subtitle">Digital Proof-of-Delivery System</p>
      </div>

      <div className="dashboard-grid">
        {menuItems.map((item, index) => (
          <div 
            key={index} 
            className="dashboard-card"
            onClick={() => navigate(item.path)}
          >
            <div className="card-icon">{item.icon}</div>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
            <div className="card-arrow">â†’</div>
          </div>
        ))}
      </div>

      <div className="dashboard-footer">
        <div className="stats-container">
          <div className="stat-card">
            <div className="stat-icon">ðŸ“¦</div>
            <div className="stat-content">
              <p className="stat-label">System Status</p>
              <p className="stat-value">Operational</p>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">OK</div>
            <div className="stat-content">
              <p className="stat-label">Version</p>
              <p className="stat-value">1.0.0</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
